package day1;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int nnum = -40;
		int pnum;
		
		if (nnum<0) {System.out.println("number "+nnum+" is negative");
		pnum = nnum*-1;
		System.out.println("postive number for "+nnum+" is "+pnum);}
		
		else {System.out.println("number "+nnum+" is positive");

	}

}
}